$(document).ready(function(){dynamicClearfix()});addResize("dynamicClearfix");function dynamicClearfix(){var counter=0,divider=$(".clearfix"),$rowContainer=$(".listing-entries .row");if(pageType==="md"||pageType==="lg"){$rowContainer.find(divider).remove();$rowContainer.find("> div").each(function(){counter++;if(counter%4==0){$(this).after('<div class="clearfix"></div>')}})}else if(pageType==="sm"){$rowContainer.find(divider).remove();$rowContainer.find("> div").each(function(){counter++;if(counter%3==0){$(this).after('<div class="clearfix"></div>')}})}else{$rowContainer.find(divider).remove()}}